-- title: Pitfall Remake
-- author: Shuang and Henning
-- desc: The player can walk across the screen  
-- script: lua

t=0
x=40
y=40
init=66

function init()
	cam={x=40,y=40}
	 p={x=120,y=64,spr=66} --player

end

function sign(n) 
return n>0 and 1 or n<0 and -1 or 0 end
function lerp(a,b,t) 
return (1-t)*a + t*b end

init()
function TIC()
	cls(0)		
	red()
	print("Hello World",84,84)
	print("Hello!")
	print("Walk around this world",10,6)
	p.spr=init
	cam.x=math.min(120,lerp(cam.x,120-p.x,0.05))
	cam.y=math.min(64,lerp(cam.y,64-p.y,0.05))
	t=t+3

	dx,dy=cam.x-120,cam.y-64
	local ccx=cam.x/8+(cam.x%8==0 and 1 or 0)
	local ccy=cam.y/8+(cam.y%8==0 and 1 or 0)
	map(15-ccx,8-ccy,31,18,(cam.x%8)-8,(cam.y%8)-8,0)
	spr(p.spr,p.x+cam.x,p.y+cam.y)
end

function red()
	init=66
	if btn(0) then
			y=y-1
			init=81+t%60//30*2
	end
	if btn(1) then 		
		y=y+1
		init=65+t%60//30*2
	end
	
	if btn(2) then 
			p.x=p.x-1
			init=49+t%60//30*2
	end
	
	if btn(3) then 
			p.x=p.x+1
			init=33+t%60//30*2

	end
end


